package api

import (
	"neophora/lib/type/uintval"
	"neophora/var/stderr"
)

// GetBlockByBlockHeightInHex ...
// as an example:
//
// ```
// $ curl https://example.neophora.io -d '{"jsonrpc": "2.0","id": 1,"method": "GetBlockByBlockHeightInHex","params":{"BlockHeight":2100000}}'
// {"id":1,"result":"","error":null}
// ```
func (me *T) GetBlockByBlockHeightInHex(args struct {
	BlockHeight uintval.T
}, ret *string) error {
	if args.BlockHeight.Valid() == false {
		return stderr.ErrInvalidArgs
	}
	return me.Data.GetArgsInHex(struct {
		Target string
		Index  string
		Keys   []string
	}{
		Target: "bins.blk",
		Index:  "uint.hgt",
		Keys:   []string{args.BlockHeight.Hex()},
	}, ret)
}
