package api

import "neophora/lib/type/h256"

// GetHeaderByBlockHashInHex ...
// as an example:
//
// ```
// $ curl https://example.neophora.io -d '{"jsonrpc": "2.0","id": 1,"method": "GetHeaderByBlockHashInHex","params":{"BlockHash":"405b0dcc1704505feebd6f7a2121e3c979b949827bb1fa1eaf29a205086c156b"}}'
// {"id":1,"result":"","error":null}
// ```
func (me *T) GetHeaderByBlockHashInHex(args struct {
	BlockHash h256.T
}, ret *string) error {
	return me.Data.GetArgsInHex(struct {
		Target string
		Index  string
		Keys   []string
	}{
		Target: "bins.hdr",
		Index:  "h256.blk",
		Keys:   []string{args.BlockHash.Val()},
	}, ret)
}
